#include <stdio.h>
#include <time.h>
int Burbuja(int arr[]){ //inicializamos la funcion del algorimo burbuja
int i,j;                //Variables contadoras del ciclo.
int temp=0;             //Variable temporal.
int Nelementos=2000; //Numero de elementos del arreglo
for (i=1;i<Nelementos;i++) //For para realizar el moviemineto en el arreglo
{
       for (j=0; j < Nelementos-i ;j++) // for(j=0; j < Nelementos-i; j++) es menor y no menor igual
       {
          if (arr[j] > arr[j+1])//Condicion mayor-menor para realizar el cambio de posicion
          {
            temp=arr[j];
            arr[j]=arr[j+1]; //Acciones para realizar el cambio de posicion aguadando en nuestra temoporal las posisciones
            arr[j+1]=temp;
          }
       }
}
	printf("Tu arreglo burbuja es: ");
	for(i=2;i<Nelementos;i++){  //Salida de datos en pantalla con el arreglo ya ordenado
		printf("\t%d\t",arr[i]);
	}
}
int seleccion(int arr[]){ //Funcion para el algorimo de seleccion
		int i,j,cam,posicion; //inicializacion de variables y contadores
	int n=2000; //Tamaño de nuestro arreglo
	for(i=0;i<n-1;i++){ //For para movernos de igual manera por el arreglo
		posicion=i; //Se guarda la poscion 
		for(j=i+1;j<n;j++){ //For para movemos con el otro elemento del arreglo
			if(arr[posicion]>arr[j]) //Condicion para realizar el cambio de elementos y posiciones 
				posicion=j;
			
			
			if(posicion!=i){
				cam=arr[i];
				arr[i]=arr[posicion];
				arr[posicion]=cam;
			}
		}
	
	}
		printf("\nTu arreglo con seleccion es: ");
		for(i=2;i<n;i++){//Impresion de datos en pantalla con el arreglo ya ordenado
			printf("\t%d\t",arr[i]);
			
	}
}
int inser(int arr[]){//Funcion para el ordenamientod e insercion
	int n=2000; //Tamaño del arreglo
	int i; //Contador
	int j;//Contador
	int temp;//Variable temporal para realizar la insercion
	for (i=1; i<n; i++){ //Movimiento dentro del arreglo
		temp = arr[i];//se guarda la posicion del primer contador
		j= i - 1;
		while ((arr[j]>temp)&&(j>=0))//Condiciones para poder realizar la insercion de numero pequeño
			arr[j+1] = arr[j];//Se realiza la insercion ya que encontro un numero mas pequeño
			j--;//retrocede
		arr[j+1] = temp;
	}
	printf("\nTu arreglo con inserccion es: ");
		for(i=1;i<n-1;i++){//salida de datos en pantalla
			printf("\t%d\t",arr[i]);
	}

}
